  event?: string;
  since?: Date;
  userId?: string;
}): Promise<AuditEntry[]> {
  let entries = [...auditLog];
  if (filters?.event) {
    entries = entries.filter(e => e.event === filters.event);
  }
  if (filters?.since) {
    entries = entries.filter(e => e.timestamp >= filters.since!);
  }
  if (filters?.userId) {
    entries = entries.filter(e => e.data.userId === filters.userId);
  }
  return entries;
}

