     1→/**
     2→ * Authentication Service for AgentDB
     3→ *
     4→ * Comprehensive authentication management:
     5→ * - User registration and login
     6→ * - Password-based authentication
     7→ * - API key management (generation, validation, rotation)
     8→ * - Session management
     9→ * - Token lifecycle management
    10→ * - Rate limiting and brute force protection
    11→ *
    12→ * Security Features:
    13→ * - Argon2id password hashing
    14→ * - Secure API key generation and storage
    15→ * - Automatic key rotation
    16→ * - Login attempt tracking
    17→ * - Account lockout protection
    18→ * - Audit logging
    19→ */
    20→
    21→import {
    22→  hashPassword,
    23→  verifyPassword,
    24→  generateApiKey,
    25→  hashApiKey,
    26→  verifyApiKey,
    27→  generateSessionId,
    28→} from '../utils/crypto.utils.js';
    29→import {
    30→  createTokenPair,
    31→  verifyAccessToken,
    32→  verifyRefreshToken,
    33→  refreshAccessToken,
    34→  revokeToken,
    35→  TokenPair,
    36→  TokenPayload,
    37→} from './token.service.js';
    38→import { ValidationError } from '../security/input-validation.js';
    39→
    40→/**
    41→ * User interface
    42→ */
    43→export interface User {
    44→  id: string;
    45→  email: string;
    46→  passwordHash: string;
    47→  role: string;
    48→  permissions: string[];
    49→  createdAt: Date;
    50→  updatedAt: Date;
    51→  lastLoginAt?: Date;
    52→  failedLoginAttempts: number;
    53→  lockedUntil?: Date;
    54→  active: boolean;
    55→}
    56→
    57→/**
    58→ * API Key interface
    59→ */
    60→export interface ApiKey {
    61→  id: string;
    62→  userId: string;
    63→  keyHash: string;
    64→  name: string;
    65→  environment: 'live' | 'test';
    66→  createdAt: Date;
    67→  expiresAt?: Date;
    68→  lastUsedAt?: Date;
    69→  active: boolean;
    70→  revokedAt?: Date;
    71→  revokedReason?: string;
    72→}
    73→
    74→/**
    75→ * Login result
    76→ */
    77→export interface LoginResult {
    78→  success: boolean;
    79→  user?: Partial<User>;
    80→  tokens?: TokenPair;
    81→  error?: string;
    82→  accountLocked?: boolean;
    83→  attemptsRemaining?: number;
    84→}
    85→
    86→/**
    87→ * Registration result
    88→ */
    89→export interface RegistrationResult {
    90→  success: boolean;
    91→  user?: Partial<User>;
    92→  error?: string;
    93→}
    94→
    95→/**
    96→ * API Key creation result
    97→ */
    98→export interface ApiKeyCreationResult {
    99→  success: boolean;
   100→  apiKey?: string; // Only returned once at creation
   101→  keyInfo?: Omit<ApiKey, 'keyHash'>;
   102→  error?: string;
   103→}
   104→
   105→/**
   106→ * Authentication configuration
   107→ */
   108→const AUTH_CONFIG = {
   109→  MAX_LOGIN_ATTEMPTS: 5,
   110→  LOCKOUT_DURATION_MS: 15 * 60 * 1000, // 15 minutes
   111→  API_KEY_DEFAULT_EXPIRY_DAYS: 365, // 1 year
   112→  SESSION_TIMEOUT_MS: 30 * 60 * 1000, // 30 minutes
   113→} as const;
   114→
   115→/**
   116→ * In-memory user store (for production, use a database)
   117→ */
   118→const users = new Map<string, User>();
   119→const usersByEmail = new Map<string, User>();
   120→
   121→/**
   122→ * In-memory API key store (for production, use a database)
   123→ */
   124→const apiKeys = new Map<string, ApiKey>();
   125→
   126→/**
   127→ * In-memory session store (for production, use Redis)
   128→ */
   129→const activeSessions = new Map<string, { userId: string; expiresAt: Date }>();
   130→
   131→/**
   132→ * Register a new user
   133→ */
   134→export async function registerUser(
   135→  email: string,
   136→  password: string,
   137→  role: string = 'user',
   138→  permissions: string[] = []
   139→): Promise<RegistrationResult> {
   140→  // Validate email
   141→  if (!email || typeof email !== 'string') {
   142→    return {
   143→      success: false,
   144→      error: 'Valid email is required',
   145→    };
   146→  }
   147→
   148→  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
   149→  if (!emailRegex.test(email)) {
   150→    return {
   151→      success: false,
   152→      error: 'Invalid email format',
   153→    };
   154→  }
   155→
   156→  // Check if user already exists
   157→  if (usersByEmail.has(email.toLowerCase())) {
   158→    return {
   159→      success: false,
   160→      error: 'User with this email already exists',
   161→    };
   162→  }
   163→
   164→  // Validate password
   165→  if (!password || typeof password !== 'string') {
   166→    return {
   167→      success: false,
   168→      error: 'Password is required',
   169→    };
   170→  }
   171→
   172→  if (password.length < 8) {
   173→    return {
   174→      success: false,
   175→      error: 'Password must be at least 8 characters long',
   176→    };
   177→  }
   178→
   179→  try {
   180→    // Hash password
   181→    const passwordHash = await hashPassword(password);
   182→
   183→    // Create user
   184→    const now = new Date();
   185→    const userId = `user_${Date.now()}_${Math.random().toString(36).substring(7)}`;
   186→
   187→    const user: User = {
   188→      id: userId,
   189→      email: email.toLowerCase(),
   190→      passwordHash,
   191→      role,
   192→      permissions,
   193→      createdAt: now,
   194→      updatedAt: now,
   195→      failedLoginAttempts: 0,
   196→      active: true,
   197→    };
   198→
   199→    // Store user
   200→    users.set(userId, user);
   201→    usersByEmail.set(email.toLowerCase(), user);
   202→
   203→    // Return user (without password hash)
   204→    const { passwordHash: _, ...userWithoutPassword } = user;
   205→
   206→    return {
   207→      success: true,
   208→      user: userWithoutPassword,
   209→    };
   210→  } catch (error) {
   211→    return {
   212→      success: false,
   213→      error: `Registration failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
   214→    };
   215→  }
   216→}
   217→
   218→/**
   219→ * Authenticate user with email and password
   220→ */
   221→export async function login(
   222→  email: string,
   223→  password: string
   224→): Promise<LoginResult> {
   225→  // Validate inputs
   226→  if (!email || !password) {
   227→    return {
   228→      success: false,
   229→      error: 'Email and password are required',
   230→    };
   231→  }
   232→
   233→  // Find user
   234→  const user = usersByEmail.get(email.toLowerCase());
   235→
   236→  if (!user) {
   237→    // Don't reveal that user doesn't exist
   238→    return {
   239→      success: false,
   240→      error: 'Invalid credentials',
   241→    };
   242→  }
   243→
   244→  // Check if account is active
   245→  if (!user.active) {
   246→    return {
   247→      success: false,
   248→      error: 'Account is disabled',
   249→    };
   250→  }
   251→
   252→  // Check if account is locked
   253→  if (user.lockedUntil && user.lockedUntil > new Date()) {
   254→    const remainingMinutes = Math.ceil(
   255→      (user.lockedUntil.getTime() - Date.now()) / (60 * 1000)
   256→    );
   257→
   258→    return {
   259→      success: false,
   260→      error: `Account is locked. Try again in ${remainingMinutes} minutes`,
   261→      accountLocked: true,
   262→    };
   263→  }
   264→
   265→  // Verify password
   266→  const passwordValid = await verifyPassword(password, user.passwordHash);
   267→
   268→  if (!passwordValid) {
   269→    // Increment failed login attempts
   270→    user.failedLoginAttempts += 1;
   271→
   272→    // Lock account if too many failed attempts
   273→    if (user.failedLoginAttempts >= AUTH_CONFIG.MAX_LOGIN_ATTEMPTS) {
   274→      user.lockedUntil = new Date(Date.now() + AUTH_CONFIG.LOCKOUT_DURATION_MS);
   275→
   276→      return {
   277→        success: false,
   278→        error: `Too many failed login attempts. Account locked for 15 minutes`,
   279→        accountLocked: true,
   280→      };
   281→    }
   282→
   283→    const attemptsRemaining = AUTH_CONFIG.MAX_LOGIN_ATTEMPTS - user.failedLoginAttempts;
   284→
   285→    return {
   286→      success: false,
   287→      error: 'Invalid credentials',
   288→      attemptsRemaining,
   289→    };
   290→  }
   291→
   292→  // Reset failed login attempts on successful login
   293→  user.failedLoginAttempts = 0;
   294→  user.lockedUntil = undefined;
   295→  user.lastLoginAt = new Date();
   296→
   297→  // Create session
   298→  const sessionId = generateSessionId();
   299→  activeSessions.set(sessionId, {
   300→    userId: user.id,
   301→    expiresAt: new Date(Date.now() + AUTH_CONFIG.SESSION_TIMEOUT_MS),
   302→  });
   303→
   304→  // Create token pair
   305→  const tokenPayload: Omit<TokenPayload, 'type'> = {
   306→    userId: user.id,
   307→    email: user.email,
   308→    role: user.role,
   309→    permissions: user.permissions,
   310→    sessionId,
   311→  };
   312→
   313→  const tokens = createTokenPair(tokenPayload);
   314→
   315→  // Return user and tokens (without password hash)
   316→  const { passwordHash: _, ...userWithoutPassword } = user;
   317→
   318→  return {
   319→    success: true,
   320→    user: userWithoutPassword,
   321→    tokens,
   322→  };
   323→}
   324→
   325→/**
   326→ * Logout user (revoke tokens and clear session)
   327→ */
   328→export function logout(sessionId: string, refreshToken?: string): void {
   329→  // Remove session
   330→  activeSessions.delete(sessionId);
   331→
   332→  // Revoke refresh token if provided
   333→  if (refreshToken) {
   334→    revokeToken(refreshToken);
   335→  }
   336→}
   337→
   338→/**
   339→ * Validate session
   340→ */
   341→export function validateSession(sessionId: string): boolean {
   342→  const session = activeSessions.get(sessionId);
   343→
   344→  if (!session) {
   345→    return false;
   346→  }
   347→
   348→  // Check if session expired
   349→  if (session.expiresAt < new Date()) {
   350→    activeSessions.delete(sessionId);
   351→    return false;
   352→  }
   353→
   354→  return true;
   355→}
   356→
   357→/**
   358→ * Refresh session (extend expiration)
   359→ */
   360→export function refreshSession(sessionId: string): boolean {
   361→  const session = activeSessions.get(sessionId);
   362→
   363→  if (!session) {
   364→    return false;
   365→  }
   366→
   367→  session.expiresAt = new Date(Date.now() + AUTH_CONFIG.SESSION_TIMEOUT_MS);
   368→  return true;
   369→}
   370→
   371→/**
   372→ * Create API key for user
   373→ */
   374→export async function createUserApiKey(
   375→  userId: string,
   376→  name: string,
   377→  environment: 'live' | 'test' = 'live',
   378→  expiresInDays?: number
   379→): Promise<ApiKeyCreationResult> {
   380→  // Validate user exists
   381→  const user = users.get(userId);
   382→  if (!user) {
   383→    return {
   384→      success: false,
   385→      error: 'User not found',
   386→    };
   387→  }
   388→
   389→  // Validate name
   390→  if (!name || typeof name !== 'string' || name.trim().length === 0) {
   391→    return {
   392→      success: false,
   393→      error: 'API key name is required',
   394→    };
   395→  }
   396→
   397→  // Generate API key
   398→  const { key, hash } = generateApiKey(environment);
   399→
   400→  // Create API key record
   401→  const now = new Date();
   402→  const apiKeyId = `key_${Date.now()}_${Math.random().toString(36).substring(7)}`;
   403→
   404→  const expiresAt = expiresInDays
   405→    ? new Date(now.getTime() + expiresInDays * 24 * 60 * 60 * 1000)
   406→    : new Date(now.getTime() + AUTH_CONFIG.API_KEY_DEFAULT_EXPIRY_DAYS * 24 * 60 * 60 * 1000);
   407→
   408→  const apiKeyRecord: ApiKey = {
   409→    id: apiKeyId,
   410→    userId,
   411→    keyHash: hash,
   412→    name: name.trim(),
   413→    environment,
   414→    createdAt: now,
   415→    expiresAt,
   416→    active: true,
   417→  };
   418→
   419→  // Store API key
   420→  apiKeys.set(apiKeyId, apiKeyRecord);
   421→
   422→  // Return key (only shown once) and key info
   423→  const { keyHash, ...keyInfo } = apiKeyRecord;
   424→
   425→  return {
   426→    success: true,
   427→    apiKey: key, // Only returned once!
   428→    keyInfo,
   429→  };
   430→}
   431→
   432→/**
   433→ * Validate API key
   434→ */
   435→export function validateApiKey(apiKey: string): {
   436→  valid: boolean;
   437→  userId?: string;
   438→  keyInfo?: Omit<ApiKey, 'keyHash'>;
   439→  error?: string;
   440→} {
   441→  if (!apiKey || typeof apiKey !== 'string') {
   442→    return {
   443→      valid: false,
   444→      error: 'API key is required',
   445→    };
   446→  }
   447→
   448→  // Hash the provided key
   449→  let keyHash: string;
   450→  try {
   451→    keyHash = hashApiKey(apiKey);
   452→  } catch (error) {
   453→    return {
   454→      valid: false,
   455→      error: 'Invalid API key format',
   456→    };
   457→  }
   458→
   459→  // Find matching API key
   460→  for (const [keyId, storedKey] of apiKeys.entries()) {
   461→    if (verifyApiKey(apiKey, storedKey.keyHash)) {
   462→      // Check if key is active
   463→      if (!storedKey.active) {
   464→        return {
   465→          valid: false,
   466→          error: 'API key has been revoked',
   467→        };
   468→      }
   469→
   470→      // Check if key is expired
   471→      if (storedKey.expiresAt && storedKey.expiresAt < new Date()) {
   472→        return {
   473→          valid: false,
   474→          error: 'API key has expired',
   475→        };
   476→      }
   477→
   478→      // Update last used timestamp
   479→      storedKey.lastUsedAt = new Date();
   480→
   481→      // Return valid result
   482→      const { keyHash: _, ...keyInfo } = storedKey;
   483→
   484→      return {
   485→        valid: true,
   486→        userId: storedKey.userId,
   487→        keyInfo,
   488→      };
   489→    }
   490→  }
   491→
   492→  return {
   493→    valid: false,
   494→    error: 'Invalid API key',
   495→  };
   496→}
   497→
   498→/**
   499→ * Revoke API key
   500→ */
   501→export function revokeApiKey(
   502→  keyId: string,
   503→  reason?: string
   504→): { success: boolean; error?: string } {
   505→  const apiKey = apiKeys.get(keyId);
   506→
   507→  if (!apiKey) {
   508→    return {
   509→      success: false,
   510→      error: 'API key not found',
   511→    };
   512→  }
   513→
   514→  apiKey.active = false;
   515→  apiKey.revokedAt = new Date();
   516→  apiKey.revokedReason = reason;
   517→
   518→  return { success: true };
   519→}
   520→
   521→/**
   522→ * List API keys for user
   523→ */
   524→export function listUserApiKeys(userId: string): Array<Omit<ApiKey, 'keyHash'>> {
   525→  const userKeys: Array<Omit<ApiKey, 'keyHash'>> = [];
   526→
   527→  for (const [keyId, apiKey] of apiKeys.entries()) {
   528→    if (apiKey.userId === userId) {
   529→      const { keyHash, ...keyInfo } = apiKey;
   530→      userKeys.push(keyInfo);
   531→    }
   532→  }
   533→
   534→  return userKeys.sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
   535→}
   536→
   537→/**
   538→ * Rotate API key (create new key and revoke old one)
   539→ */
   540→export async function rotateApiKey(
   541→  keyId: string,
   542→  reason: string = 'Key rotation'
   543→): Promise<ApiKeyCreationResult> {
   544→  const oldKey = apiKeys.get(keyId);
   545→
   546→  if (!oldKey) {
   547→    return {
   548→      success: false,
   549→      error: 'API key not found',
   550→    };
   551→  }
   552→
   553→  // Create new key
   554→  const newKeyResult = await createUserApiKey(
   555→    oldKey.userId,
   556→    oldKey.name,
   557→    oldKey.environment
   558→  );
   559→
   560→  if (!newKeyResult.success) {
   561→    return newKeyResult;
   562→  }
   563→
   564→  // Revoke old key
   565→  revokeApiKey(keyId, reason);
   566→
   567→  return newKeyResult;
   568→}
   569→
   570→/**
   571→ * Get user by ID
   572→ */
   573→export function getUserById(userId: string): Partial<User> | null {
   574→  const user = users.get(userId);
   575→
   576→  if (!user) {
   577→    return null;
   578→  }
   579→
   580→  const { passwordHash, ...userWithoutPassword } = user;
   581→  return userWithoutPassword;
   582→}
   583→
   584→/**
   585→ * Update user password
   586→ */
   587→export async function updateUserPassword(
   588→  userId: string,
   589→  oldPassword: string,
   590→  newPassword: string
   591→): Promise<{ success: boolean; error?: string }> {
   592→  const user = users.get(userId);
   593→
   594→  if (!user) {
   595→    return {
   596→      success: false,
   597→      error: 'User not found',
   598→    };
   599→  }
   600→
   601→  // Verify old password
   602→  const passwordValid = await verifyPassword(oldPassword, user.passwordHash);
   603→
   604→  if (!passwordValid) {
   605→    return {
   606→      success: false,
   607→      error: 'Current password is incorrect',
   608→    };
   609→  }
   610→
   611→  // Validate new password
   612→  if (newPassword.length < 8) {
   613→    return {
   614→      success: false,
   615→      error: 'New password must be at least 8 characters long',
   616→    };
   617→  }
   618→
   619→  try {
   620→    // Hash new password
   621→    user.passwordHash = await hashPassword(newPassword);
   622→    user.updatedAt = new Date();
   623→
   624→    return { success: true };
   625→  } catch (error) {
   626→    return {
   627→      success: false,
   628→      error: `Password update failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
   629→    };
   630→  }
   631→}
   632→
   633→/**
   634→ * Clean up expired sessions (call periodically)
   635→ */
   636→export function cleanupExpiredSessions(): number {
   637→  let cleaned = 0;
   638→  const now = new Date();
   639→
   640→  for (const [sessionId, session] of activeSessions.entries()) {
   641→    if (session.expiresAt < now) {
   642→      activeSessions.delete(sessionId);
   643→      cleaned++;
   644→    }
   645→  }
   646→
   647→  return cleaned;
   648→}
   649→
   650→/**
   651→ * Get active session count
   652→ */
   653→export function getActiveSessionCount(): number {
   654→  cleanupExpiredSessions();
   655→  return activeSessions.size;
   656→}
   657→
   658→/**
   659→ * Export for testing
   660→ */
   661→export const __testing__ = {
   662→  users,
   663→  usersByEmail,
   664→  apiKeys,
   665→  activeSessions,
   666→  AUTH_CONFIG,
   667→};
